import {useState, useEffect} from "react";
import Fuse from "fuse.js";
import axios from 'axios';

// import kb_list from './kb_list.json';
import SearchList from './Components/SearchList';
import AvailableProductList from './Components/AvailableProductList';
import Header from './Components/Header';
import Microphone from './Components/Microphone';

import classes from './App.module.css'

// const kb_list = [];

function App() {
  
  const [searchState, setSearchState] = useState('');
  const [initialkblist, setinitialkblist]= useState([])
  const [kblist, setkblist] = useState([]);
  const [isListening, setisListening] = useState(false);
  const [counter, setCounter] = useState(5);
  const [searchResults, setsearchResults]= useState([]);
  
  useEffect(()=>{
    
    axios.get('https://kb-search-engine-default-rtdb.firebaseio.com/kblist.json')
      .then(response=>{
        setinitialkblist(response.data);
        setkblist(response.data)
      })
  }, [])
  
  useEffect(()=>{
    const fuse = new Fuse(kblist, {keys: ["scenario"], includeScore: true});
    setsearchResults(fuse.search(searchState).map((r)=> r.item));
    
  }, [kblist, searchState])
  
  useEffect(()=>{
    if(isListening===true){
      setTimeout(()=>setCounter(counter - 1), 1000)
    } else{ setCounter(5)}
    // return clearTimeout(time);
  }, [counter, isListening]);
  
  

  function onSearchInput(e){
    setSearchState(e.target.value);
  }

  function onMicStart (transcript){
    setSearchState(transcript);
  }

  function onClearIconClick(){
    setSearchState('');
  }

  function onProdClick (prodName){
    setkblist(prodName === "Search All"? initialkblist : initialkblist.filter((item)=> item.product === prodName));
  }

  function onListening(value){
    setisListening(value);
  }


  return (
      <div className = {classes.wrapper}>
        <div>
          <Header />
          <h4 style= {{marginTop: "25px"}}>Available Products :</h4>
          <AvailableProductList products = {initialkblist} onProdClick = {onProdClick} />
        </div>

        <div style ={{flex : 1,  margin: "22px 30px 0 130px"}}>
          <div className={classes.searchwrapper}>
            <img 
              className={classes.searchicon} alt="Search Icon" 
              src="data:image/svg+xml;utf8;base64," />
            <input 
              type= "text" 
              className = {classes.search} 
              placeholder= "Click on the Product in the sidebar and then either type the error scenario or Click on Mic and speak" 
              value = {searchState} 
              onChange= {onSearchInput} />
            <img 
              className= {searchState==="" ? classes["clear-icon"] : classes.cleariconvisible}
              onClick = {onClearIconClick} alt="Cancel Icon"
              src="data:image/svg+xml;utf8;base64," /> 
              <Microphone onListening= {onListening}  onClick= {onMicStart} /> 
              
          </div>
          
            <div className= {classes.listentext} >{isListening ? "Listening, Speak now......" + counter: ""}</div>
            <SearchList searchResults = {searchResults} />

          </div>
          
      </div>  

  );
}

export default App;

